'   -- number of characters from the clob show on the left and right when a match in the notes section is found (to give context for the match).',
'   clob_match_padding pls_integer := 50;',
'   --',
'   c pls_integer := 0;',
'   l_detail varchar2(4000);',
'   i pls_integer;',
'   l pls_integer;',
'   l_clob pls_integer;',
'   l_max_rows    integer;',
'   s_agree_row varchar2(4000);',
'  -- i_clob_match pls_integer;',
'  -- clob_html_strip clob;',
'   --array of space delimited search parameters;',
'   -- search_array t_array;',
'begin',
'l_max_rows := nvl(:P6_ROWS,10);',
'if :P6_SEARCH is null ',
'and :P6_VENDOR is null ',
'and :P6_DEPARTMENT is null',
'and :P6_RENEWABLE_LOV is null',
'and :P6_START_DATE is null ',
'and :P6_END_DATE is null',
'and :P6_APPROVALS is null ',
'and (:P6_FINAL is null or lower(:P6_FINAL) = ''n'')',
'and (:P6_SHOW_VERSIONS is null or lower(:P6_SHOW_VERSIONS) = ''n'')',
'and (:P6_EXPIRING_IN_YEAR is null)',
'and :P6_PENDING_APPROVALS is null',
'and :P6_RECEIVED_APPROVALS is null',
'and :P6_TRIGGERS is null',
'and :P6_VERSION_NUMBER is null then',
'   sys.htp.p(''<p>''||',
'       apex_escape.html(''Please enter at least one search condition.'')||',
'       ''</p>'');',
'else',
'  sys.htp.p(''<p>inelse.</p>'');',
'  -- Loop through space delimited search results array ',
'  --',
'  -- search_array := split( :P6_SEARCH, '','' );',
'  --',
'  -- Loop through all search matches',
'  --',
'  for c1 in (',
'  select a.agreement_id aid, ',
'  a.contract_number contract_number, ',
'  (select name from UTBC_VENDOR where a.vendor_id = vendor_id) vname, ',
'  (select name from UTBC_DEPARTMENT where a.department_id = department_id) dname, ',
'  a.start_date start_date, ',
'  a.end_date end_date, ',
'  a.purpose purpose, ',
'  a.renewable renewable,',
'  --if need be, add condition to disable clob searching for performance  ',
'  no_html(a.note) note,',
'  a.final final,',
'  a.version version,',
'  a.type type',
'  from UTBC_AGREEMENTS a --, UTBC_AGREE_APP p',
'  where',
'      -- search filters',
'       (upper(a.renewable) = upper(:P6_RENEWABLE_LOV) or :P6_RENEWABLE_LOV is NULL)',
'       --and (a.vendor_id = :P6_VENDOR or :P6_VENDOR is NULL)',
'       and (instr(upper(:P6_VENDOR), upper(''v''||a.vendor_id||''v'')) > 0 or :P6_VENDOR is null)',
'       and (instr(upper(:P6_VENDOR), upper(''v''||a.vendor_id||''v'')) > 0 or :P6_VENDOR is null)',
'       --and (a.department_id = :P6_DEPARTMENT or :P6_DEPARTMENT is NULL)',
'       and (instr(upper(:P6_DEPARTMENT), upper(''d''||a.department_id||''d'')) > 0 or :P6_DEPARTMENT is null)',
'       and (a.start_date = :P6_START_DATE or :P6_START_DATE is null)',
'       and (a.end_date = :P6_END_DATE or :P6_END_DATE is null)',
'       and ((lower(:P6_EXPIRING_IN_YEAR) = ''y'' and a.end_date < :P6_NEXT_FISCAL_YEAR) ',
'         OR (lower(:P6_EXPIRING_IN_YEAR) = ''n'' and a.end_date > :P6_NEXT_FISCAL_YEAR)',
'         OR :P6_EXPIRING_IN_YEAR is NULL)',
'       and ((lower(:P6_FINAL) = ''y'' AND lower(a.final) = ''y'')',
'         OR lower(:P6_FINAL) = ''n''',
'         OR :P6_FINAL is null)',
'       --show only max version if :P6_SHOW_VERSIONS filter option selected',
'       and ((:P6_SHOW_VERSIONS = ''n'' AND a.version = ',
'         (SELECT max(version) FROM UTBC_agreements WHERE version_parent = a.version_parent))',
'         OR :P6_SHOW_VERSIONS = ''y''',
'         OR :P6_SHOW_VERSIONS is null)',
'--       and (a.version = :P6_VERSION_NUMBER or :P6_VERSION_NUMBER is null)',
'--       --check if this agreement has at least 1 entry for an approval in the P6_APPROVALS multiselect LOV',
'--       and ((select count(p.agree_app_id) from UTBC_agree_app p ',
'--             where p.agreement_id = a.agreement_id',
'--             and upper(p.complete) = ''N''',
'--             and instr(upper(:P6_PENDING_APPROVALS), upper(''a''||p.approval_id||''a'')) > 0) > 0',
'--         OR :P6_PENDING_APPROVALS is null)',
'--       and ((select count(p.agree_app_id) from UTBC_agree_app p ',
'--             where p.agreement_id = a.agreement_id ',
'--             and upper(p.complete) = ''Y''',
'--             and instr(upper(:P6_RECEIVED_APPROVALS), upper(''a''||p.approval_id||''a'')) > 0) > 0',
'--         OR :P6_RECEIVED_APPROVALS is null)',
'--       and ((select count(t.agree_trig_id) from UTBC_agree_trig t ',
'--             where t.agreement_id = a.agreement_id ',
'--             and instr(upper(:P6_TRIGGERS), upper(''t''||t.trigger_id||''t'')) > 0) > 0',
'--         OR :P6_TRIGGERS is null)',
'--       and ( --search :P6_SEARCH TEXT',
'--       instr(upper(a.contract_number),upper(:P6_SEARCH)) > 0 ',
'--       or instr(upper((select name from UTBC_VENDOR where a.vendor_id = vendor_id)),upper(:P6_SEARCH)) > 0',
'--       or instr(upper((select name from UTBC_DEPARTMENT where a.department_id = department_id)),upper(:P6_SEARCH)) > 0',
'--       or instr(upper(a.purpose), upper(:P6_SEARCH)) > 0',
'--          --search through clob note using DBMS_LOB.INSTR',
'--       or DBMS_LOB.INSTR(lower(no_html(a.note)), lower(:P6_SEARCH)) > 0',
'--       or :P6_SEARCH is null)',
'      ',
'  order by contract_number, version',
'  )',
'  loop',
'    sys.htp.p(''<p>begin loop.</p>'');',
'    sys.htp.p(''<p>begin loop.''||c||''</p>'');',
'    c := c + 1;',
'    sys.htp.p(''<p>outloop.</p>'');',
'    if c = 1 then',
'       sys.htp.p(''<p>inloop.</p>'');',
'       sys.htp.p(''<ul class="sSearchResultsReport">'');',
'    end if;',
'    sys.htp.p(''<li>'');',
'    sys.htp.prn(''<span class="title">'');',
'    --',
'    -- DEFINE DRILL DOWN LINK HERE',
'    --',
'    sys.htp.prn(''<a href="''',
'      ||apex_util.prepare_url(''f?p=''||:APP_ID||'':4:''||:APP_SESSION||',
'        '':::4:P4_AGREEMENT_ID:''||c1.aid)||''">'');',
'',
'    --',
'    -- ASSIGN PRIMAY ATTRIBUTE HERE, REFERENCE QUERY COLUMN',
'    --',
'    l_detail := c1.contract_number;',
'    --',
'    i := instr(upper(l_detail),upper(:P6_SEARCH));',
'    if  i > 0 then',
'       l := length(:P6_SEARCH);',
'       l_detail := apex_escape.html(substr(l_detail,1,i-1))||',
'          ''<span class="highlight">''||',
'          apex_escape.html(substr(l_detail,i,l))||''</span>''||',
'          apex_escape.html(substr(l_detail,i+l));',
'    end if;',
'    if lower(c1.final) = ''y'' then',
'      sys.htp.prn(''-FINAL- '');',
'    else',
'      sys.htp.prn(''-DRAFT v'' || c1.version || ''- '');',
'    end if;',
'    sys.htp.prn(c1.type);',
'    sys.htp.prn('' Number: '');',
'    sys.htp.prn(l_detail);                ',
'    sys.htp.prn(''</a></span>'');',
'    --',
'    --',
'    sys.htp.prn(''<span class="description">'');',
'    --',
'    -- ASSIGN SECONDARY ATTRIBUTE HERE, REFERENCE QUERY COLUMN',
'    -- ',
'    l_detail := c1.vname;',
'    --',
'    i := instr(upper(l_detail),upper(:P6_SEARCH));',
'    if  i > 0 then',
'       l := length(:P6_SEARCH);',
'       l_detail := apex_escape.html(substr(l_detail,1,i-1))||',
'          ''<span class="highlight">''||',
'          apex_escape.html(substr(l_detail,i,l))||''</span>''||',
'          apex_escape.html(substr(l_detail,i+l));',
'    end if;',
'    sys.htp.prn(''Vendor: '');',
'    sys.htp.prn(l_detail);',
'    sys.htp.prn(''</span>'');',
'    --',
'    --',
'    sys.htp.prn(''<span class="description">'');',
'    --',
'    -- ASSIGN THIRD ATTRIBUTE HERE, REFERENCE QUERY COLUMN',
'    -- ',
'    l_detail := c1.dname;',
'    --',
'    i := instr(upper(l_detail),upper(:P6_SEARCH));',
'    if  i > 0 then',
'       l := length(:P6_SEARCH);',
'       l_detail := apex_escape.html(substr(l_detail,1,i-1))||',
'          ''<span class="highlight">''||',
'          apex_escape.html(substr(l_detail,i,l))||''</span>''||',
'          apex_escape.html(substr(l_detail,i+l));',
'    end if;',
'    sys.htp.prn(''Department: '');',
'    sys.htp.prn(l_detail);',
'    sys.htp.prn(''</span>'');',
'    --',
'    --',
'    sys.htp.prn(''<span class="description">'');',
'    --',
'    -- ASSIGN FORTH ATTRIBUTE HERE, REFERENCE QUERY COLUMN',
'    -- ',
'    l_detail := c1.purpose;',
'    --',
'    i := instr(upper(l_detail),upper(:P6_SEARCH));',
'    if  i > 0 then',
'       l := length(:P6_SEARCH);',
'       l_detail := apex_escape.html(substr(l_detail,1,i-1))||',
'          ''<span class="highlight">''||',
'          apex_escape.html(substr(l_detail,i,l))||''</span>''||',
'          apex_escape.html(substr(l_detail,i+l));',
'    end if;',
'    sys.htp.prn(''Purpose: '');',
'    sys.htp.prn(l_detail);',
'    sys.htp.prn(''</span>'');',
'',
'    --',
'    -- ASSIGN FIFTH ATTRIBUTE HERE, REFERENCE QUERY COLUMN',
'    -- This only displays the note detail if the match is found within it.',
'    -- It should only display a substring of the clob where the match is found',
'    i := DBMS_LOB.INSTR(lower(c1.note), lower(:P6_SEARCH));',
'    if  i > 0 then',
'       l_clob := DBMS_LOB.GETLENGTH(c1.note);',
'       l := length(:P6_SEARCH);',
'       --if at least clob_match_padding characters exist in note on the left and right side of matched text in clob',
'       IF i > clob_match_padding and l_clob > (i+l+clob_match_padding) then',
'           l_detail := ''...'' || TO_CHAR(DBMS_LOB.SUBSTR(c1.note, l+(clob_match_padding*2), i-clob_match_padding)) || ''...'';',
'           i := clob_match_padding+4;',
'       --if at least clob_match_padding characters exist in note on the left side of matched text in clob, but not on the right.',
'       ELSIF i > clob_match_padding then',
'           l_detail := ''...'' || TO_CHAR(DBMS_LOB.SUBSTR(c1.note, (l_clob - i) + clob_match_padding + l, i-clob_match_padding));',
'           i := clob_match_padding + 4;',
'       --if at least clob_match_padding characters exist in note on the right side of matched text in clob, but not on the left.',
'       ELSIF l_clob > (i+l+clob_match_padding) then',
'           l_detail := TO_CHAR(DBMS_LOB.SUBSTR(c1.note, i+l+clob_match_padding, 1)) || ''...'';',
'       --i := 4;',
'       --note is small.  clob_match_padding characters do not exist on the left and right of matched text in clob.',
'       ELSE',
'           l_detail := TO_CHAR(c1.note);',
'       --i := i+3;',
'       END IF;',
'       sys.htp.prn(''<span class="description">'');',
'       ',
'       l_detail := apex_escape.html(substr(l_detail,1,i-1))||',
'          ''<span class="highlight">''||',
'          apex_escape.html(substr(l_detail,i,l))||''</span>''||',
'          apex_escape.html(substr(l_detail,i+l));',
'      sys.htp.prn(''Note match: '');--i: '' || TO_CHAR(i) || '' l: '' || TO_CHAR(l) || '' l_clob: '' || TO_CHAR(l_clob));',
'      sys.htp.prn(l_detail);',
'      sys.htp.prn(''</span>'');',
'    end if;',
'    --',
'    -- display additional column detail',
'    --',
'    sys.htp.p(''<span class="type">'');',
'    sys.htp.prn(''Start date: '');',
'    sys.htp.prn(apex_escape.html(c1.start_date));',
'    sys.htp.prn(''</span>'');',
'',
'    sys.htp.p(''<span class="type">'');',
'    sys.htp.prn(''End date: '');',
'    sys.htp.prn(apex_escape.html(c1.end_date));',
'    sys.htp.prn(''</span>'');',
'',
'    sys.htp.p(''<span class="type">'');',
'    sys.htp.prn(''Renewable: '');',
'    sys.htp.prn(apex_escape.html(c1.renewable));',
'    sys.htp.prn(''</span>'');',
'',
'    sys.htp.p(''<br /><span class="type">'');',
'    sys.htp.prn(''Status: '');',
'    sys.htp.prn(apex_escape.html(c1.end_date));',
'    sys.htp.prn(''</span>'');',
'    --',
'    sys.htp.prn(''</li>'');',
'    if c = l_max_rows then',
'       exit;',
'    end if;',
'',
'  end loop;',
'  sys.htp.p(:P6_VENDOR);',
'  if c > 0 then ',
'    sys.htp.p(''</ul>'');',
'    sys.htp.p(''<p>''||c||'' results found.</p>'');',
'  else ',
'    sys.htp.p(''<p>No data found matching your search criteria.</p>'');',
'  end if;',
'end if;',
'end;'))
,p_plug_source_type=>'NATIVE_PLSQL'
,p_plug_query_row_template=>1
,p_attribute_01=>'N'
,p_attribute_02=>'TEXT'
,p_attribute_03=>'Y'
);
