,p_plug_new_grid_row=>false
,p_plug_display_point=>'BODY'
,p_plug_source=>wwv_flow_utilities.join(wwv_flow_t_varchar2(
'declare',
'   -- number of characters from the clob show on the left and right when a match in the notes section is found (to give context for the match).',
'   clob_match_padding pls_integer := 50;',
'   --',
'   c pls_integer := 0;',
'   l_detail varchar2(4000);',
'   i pls_integer;',
'   l pls_integer;',
'   l_clob pls_integer;',
'   l_max_rows    integer;',
'   s_agree_row varchar2(4000);',
'  -- i_clob_match pls_integer;',
'  -- clob_html_strip clob;',
'   --array of space delimited search parameters;',
'   -- search_array t_array;',
'begin',
'l_max_rows := nvl(:P20_ROWS,10);',
'if :P20_SEARCH is null ',
'and :P20_VENDOR is null ',
'and :P20_DEPARTMENT is null',
'and :P20_RENEWABLE_LOV is null',
'and :P20_START_DATE is null ',
'and :P20_END_DATE is null',
'and :P20_APPROVALS is null ',
'and (:P20_FINAL is null or lower(:P20_FINAL) = ''n'')',
'and (:P20_SHOW_VERSIONS is null or lower(:P20_SHOW_VERSIONS) = ''n'')',
'and (:P20_EXPIRING_IN_YEAR is null)',
'and :P20_PENDING_APPROVALS is null',
'and :P20_RECEIVED_APPROVALS is null',
'and :P20_TRIGGERS is null',
'and :P20_VERSION_NUMBER is null then',
'   sys.htp.p(''<p>''||',
'       apex_escape.html(''Please enter at least one search condition.'')||',
'       ''</p>'');',
'else',
'  --',
'  -- Loop through space delimited search results array ',
'  --',
'  -- search_array := split( :P20_SEARCH, '','' );',
'  --',
'  -- Loop through all search matches',
'  --',
'  for c1 in (',
'  select a.agreement_id aid, ',
'  a.contract_number contract_number, ',
'  (select name from UTBC_VENDOR where a.vendor_id = vendor_id) vname, ',
'  (select name from UTBC_DEPARTMENT where a.department_id = department_id) dname, ',
'  a.start_date start_date, ',
'  a.end_date end_date, ',
'  a.purpose purpose, ',
'  a.renewable renewable,',
'  --if need be, add condition to disable clob searching for performance  ',
'  no_html(a.note) note,',
'  a.final final,',
'  a.version version,',
'  a.type type',
'  from UTBC_AGREEMENTS a --, UTBC_AGREE_APP p',
'  where',
'      -- search filters',
'       (upper(a.renewable) = upper(:P20_RENEWABLE_LOV) or :P20_RENEWABLE_LOV is NULL)',
'       --and (a.vendor_id = :P20_VENDOR or :P20_VENDOR is NULL)',
'       and (instr(upper(:P20_VENDOR), upper(''v''||a.vendor_id||''v'')) > 0 or :P20_VENDOR is null)',
'       --and (a.department_id = :P20_DEPARTMENT or :P20_DEPARTMENT is NULL)',
'       and (instr(upper(:P20_DEPARTMENT), upper(''d''||a.department_id||''d'')) > 0 or :P20_DEPARTMENT is null)',
'       and (a.start_date = :P20_START_DATE or :P20_START_DATE is null)',
'       and (a.end_date = :P20_END_DATE or :P20_END_DATE is null)',
'       and ((lower(:P20_EXPIRING_IN_YEAR) = ''y'' and a.end_date < :P20_NEXT_FISCAL_YEAR) ',
'         OR (lower(:P20_EXPIRING_IN_YEAR) = ''n'' and a.end_date > :P20_NEXT_FISCAL_YEAR)',
'         OR :P20_EXPIRING_IN_YEAR is NULL)',
'       and ((lower(:P20_FINAL) = ''y'' AND lower(a.final) = ''y'')',
'         OR lower(:P20_FINAL) = ''n''',
'         OR :P20_FINAL is null)',
'       --show only max version if :P20_SHOW_VERSIONS filter option selected',
'       and ((lower(:P20_SHOW_VERSIONS) = ''n'' AND a.version = ',
'         (SELECT max(version) FROM UTBC_agreements WHERE version_parent = a.version_parent))',
'         OR lower(:P20_SHOW_VERSIONS) = ''y''',
'         OR :P20_SHOW_VERSIONS is null)',
'       and (a.version = :P20_VERSION_NUMBER or :P20_VERSION_NUMBER is null)',
'       --check if this agreement has at least 1 entry for an approval in the P20_APPROVALS multiselect LOV',
'       and ((select count(p.agree_app_id) from UTBC_agree_app p ',
'             where p.agreement_id = a.agreement_id',
'             and upper(p.complete) = ''N''',
'             and instr(upper(:P20_PENDING_APPROVALS), upper(''a''||p.approval_id||''a'')) > 0) > 0',
'         OR :P20_PENDING_APPROVALS is null)',
'       and ((select count(p.agree_app_id) from UTBC_agree_app p ',
'             where p.agreement_id = a.agreement_id ',
'             and upper(p.complete) = ''Y''',
'             and instr(upper(:P20_RECEIVED_APPROVALS), upper(''a''||p.approval_id||''a'')) > 0) > 0',
'         OR :P20_RECEIVED_APPROVALS is null)',
'       and ((select count(t.agree_trig_id) from UTBC_agree_trig t ',
'             where t.agreement_id = a.agreement_id ',
'             and instr(upper(:P20_TRIGGERS), upper(''t''||t.trigger_id||''t'')) > 0) > 0',
'         OR :P20_TRIGGERS is null)',
