begin
wwv_flow_api.create_plugin(
 p_id=>900315282126876258+wwv_flow_api.g_id_offset
,p_plugin_type=>'REGION TYPE'
,p_name=>'DINH.DE.TREETABLE'
,p_display_name=>'DTreeTable'
,p_supported_ui_types=>'DESKTOP'
,p_plsql_code=>wwv_flow_utilities.join(wwv_flow_t_varchar2(
'/***************************************************************************************************',
' * DTreeTable ApEx Plugin puts hierarchical data in columns, but use an indented outline structure',
' * in the first column to illustrate the tree structure.',
' *',
' * Author: Linh Dinh (linh@dinh.de)',
' * URL: http://www.dinh.de',
' * Created On: 2010-11-06',
' * Version: 0.9 (2010-11-06)',
' ',
' * This Plugin is dual licensed under the MIT and GPL licenses:',
' * http://www.opensource.org/licenses/mit-license.php',
' * http://www.gnu.org/licenses/gpl.html',
' **************************************************************************************************/',
'',
'FUNCTION render_dtreetable(',
'    p_region              IN apex_plugin.t_region,',
'    p_plugin              IN apex_plugin.t_plugin,',
'    p_is_printer_friendly IN BOOLEAN )',
'  RETURN apex_plugin.t_region_render_result',
'IS',
'  l_col_header htmldb_application_global.vc_arr2;',
'  l_value_list apex_plugin_util.t_column_value_list;',
'  v_indenting INTEGER;',
'  v_width_1 INTEGER;',
'  v_expand CHAR(1);',
'  v_level_1 INTEGER;',
'  v_level_tmp INTEGER;',
'  v_class_display VARCHAR(30);',
'  v_class_leaf VARCHAR(30);',
'  v_version VARCHAR2(10) := ''''; --0.9',
'BEGIN',
'  v_indenting := p_region.attribute_01;',
'  l_col_header := htmldb_util.string_to_table(p_region.attribute_02, '':'');',
'  v_expand := p_region.attribute_03;',
'  v_width_1 := p_region.attribute_04;',
'  ',
'  ',
'  APEX_PLUGIN_UTIL.DEBUG_REGION ( ',
'    p_plugin, ',
'    p_region',
'  );',
'  ',
'  -- Assign javascript file',
'  apex_javascript.add_library (',
'    p_name      => ''dtt'',',
'    p_directory => p_plugin.file_prefix,',
'    p_version   => v_version',
'  );',
'    ',
'  -- Assign css file',
'  apex_css.add_file ( ',
'    p_name => ''dtt_style'', ',
'    p_directory => p_plugin.file_prefix, ',
'    p_version => v_version',
'  );',
'  ',
'  -- Get resultset from query',
'  l_value_list := apex_plugin_util.get_data ( ',
'    p_sql_statement => p_region.source, ',
'    p_min_columns => 2, ',
'    p_max_columns => 50, ',
'    p_component_name => p_region.name ',
'  );',
'  ',
'  -- Set level value for the first node',
'  v_level_1 := l_value_list(1)(1);',
'  ',
'  -- Start print table',
'  sys.htp.p(''<table id="dtt_'' ',
'    || p_region.id ',
'    || ''_table" class="dtt_table">''',
'  );',
'  ',
'  -- Start print thead',
'  IF (l_col_header.COUNT > 0) THEN',
'	  sys.htp.p(''<thead class="dtt_thead">'');',
'	  sys.htp.p(''<tr class="dtt_head_tr">'');',
'	  ',
'	  FOR i IN 1 .. l_col_header.COUNT LOOP',
'	  	sys.htp.p(''<th class="dtt_head_td">'' || l_col_header(i) || ''</th>'');',
'	  END LOOP;',
'	  ',
'	  sys.htp.p(''</tr>'');',
'	  sys.htp.p(''</thead>'');',
'  END IF;',
'  ',
'  -- Start print for tbody',
'  sys.htp.p(''<tbody id="dtt_'' ',
'    || p_region.id ',
'    || ''_tbody" class="dtt_tbody">'');',
'    ',
'  FOR i IN 1 .. l_value_list(1).count',
'  LOOP',
'    -- Check wheather the settings are collapsed or expanded on start up',
'    IF i < l_value_list(1).count THEN',
'      IF v_expand = ''Y'' OR l_value_list(1)(i) = v_level_1 THEN',
'        v_class_display := ''dtt_expanded'';',
'      ELSE',
'        v_class_display := ''dtt_collapsed'';',
'      END IF;',
'    ELSE',
'      v_class_display := ''dtt_collapsed'';',
'    END IF;',
'    ',
'    sys.htp.p(',
'      ''<tr dtt_level="'' ',
'      || sys.htf.escape_sc(l_value_list(1)(i))',
'      || ''" class="'' || v_class_display || ''_tr" rownum="''|| i || ''"> ''',
'    );',
'    ',
'    IF i < l_value_list(1).count THEN',
'      IF v_expand = ''N'' AND l_value_list(1)(i) = v_level_1 THEN',
'        v_class_display := ''dtt_collapsed'';',
'      END IF;',
'      ',
'      IF l_value_list(1)(i) < l_value_list(1)(i+1) THEN',
'        v_class_leaf := ''dtt_branch'';',
'      ELSE',
'        v_class_leaf := ''dtt_leaf'';',
'      END IF;',
'    ELSE',
'      v_class_leaf := ''dtt_leaf'';',
'    END IF;',
'    ',
'    ',
'    -- Rendering level value as first col and second value into one column',
'    sys.htp.p(',
'      ''<td class="dtt_td" style="''',
'    );',
'    ',
'    IF v_width_1 > 0 THEN',
'    	sys.htp.p(''width: '' || v_width_1 || ''px; '');',
'    END IF;',
'    ',
'    sys.htp.p(''" name="dtt_level">'');',
'    sys.htp.p(',
'      ''<span style="margin-left:'' ',
'      || (sys.htf.escape_sc(l_value_list(1)(i)) - 1) * v_indenting ',
'      || ''px; ">&#160;</span>''',
'    );',
'    sys.htp.p(''<a href="#" class="dtt_toggle" onclick="dttToggleNode('''''' || p_region.id || '''''', '' || i || '');">'');',
'    sys.htp.p(''<span class="dtt_icon '' || v_class_leaf || ''_span '' || v_class_display || ''_span">&#160;</span>'');',
'    sys.htp.p(''</a>'');',
'    sys.htp.p(',
'      sys.htf.escape_sc(l_value_list(2)(i)) ',
'      || ''</td>''',
'    );',
'    -- Render all other columns',
'    FOR j IN 3 .. l_value_list.count',
'    LOOP',
'      sys.htp.p(''<td class="dtt_td">'' || sys.htf.escape_sc(l_value_list(j)(i)) || ''</td>'');',
'    END LOOP;',
'    ',
'    sys.htp.p(''</tr>'');',
'    ',
'  END LOOP;',
'  sys.htp.p(''</tbody>'');',
'  sys.htp.p(''</table>'');',
'  RETURN NULL;',
'END render_dtreetable;',
''))
,p_render_function=>'render_dtreetable'
,p_standard_attributes=>'SOURCE_SQL:SOURCE_REQUIRED'
,p_sql_min_column_count=>2
,p_sql_max_column_count=>50
,p_sql_examples=>wwv_flow_utilities.join(wwv_flow_t_varchar2(
'SELECT level, ename, mgr, dept FROM emp',
'START WITH empno       = 7839',
'CONNECT BY prior empno = mgr'))
,p_substitute_attributes=>true
,p_subscribe_plugin_settings=>true
,p_help_text=>wwv_flow_utilities.join(wwv_flow_t_varchar2(
'<p>',
'	DTreeTable provides the possibility to render a composite of a tree view with a table view.</p>',
'<p>',
'	SQL-Example:</p>',
'<pre>',
'SELECT level, ename, mgr, deptno FROM emp',
'START WITH empno       = 7839',
'CONNECT BY prior empno = mgr',
'</pre>',
'<p>',
'	Please pay attention that the level value must be the first value of the SQL Statement.</p>',
'<p>',
'	<b>Indenting in px</b> <i>(Default: 10)</i><br />',
'	Higher level values increase margin-left in px by defined indenting value</p>',
'<p>',
'	<b>Column Header Name (Colon seperated)</b><br />',
'	Column Names will be shown in table header. The first and second column of the SQL Statement will be merge to one column.</p>',
'<pre>',
'Name:Manager Id:Department No.</pre>',
'<p>',
'	<b>Expand On Start Up</b> <i>(Default: Yes)</i><br />',
'	Defines wheather the tree will be expanded on start up or collapsed.</p>',
'<p>',
'	<b>First Column Width in px</b><br />',
'	If set, the first column will have a fix width.</p>',
''))
,p_version_identifier=>'0.9'
,p_about_url=>'http://www.dinh.de/'
);
