wwv_flow_api.create_plugin(
 p_id=>59829611794957191552+wwv_flow_api.g_id_offset
,p_plugin_type=>'DYNAMIC ACTION'
,p_name=>'COM_ORACLE_DE_CCZARSKI_APEXTREE_MARKUP'
,p_display_name=>'HTML Markup for APEX Tree'
,p_category=>'INIT'
,p_supported_ui_types=>'DESKTOP'
,p_plsql_code=>wwv_flow_utilities.join(wwv_flow_t_varchar2(
'function render_tree_markup(',
'    p_dynamic_action in apex_plugin.t_dynamic_action,',
'    p_plugin         in apex_plugin.t_plugin )',
'    return apex_plugin.t_dynamic_action_render_result',
'is',
'    l_open_pattern  varchar2(10) := p_dynamic_action.attribute_01;',
'    l_close_pattern varchar2(10) := p_dynamic_action.attribute_02;',
'    l_amp_pattern   varchar2(10) := p_dynamic_action.attribute_03;',
'    l_unbind_events varchar2(10) := p_dynamic_action.attribute_04;',
'    l_result        apex_plugin.t_dynamic_action_render_result;',
'begin',
'  l_open_pattern  := regexp_replace(l_open_pattern,  ''([]{}[.^$*+()\/])'', ''\\\1'');',
'  l_close_pattern := regexp_replace(l_close_pattern, ''([]{}[.^$*+()\/])'', ''\\\1'');',
'  l_amp_pattern   := regexp_replace(l_amp_pattern,   ''([]{}[.^$*+()\/])'', ''\\\1'');',
'  l_result.javascript_function :=',
'   ''function(){''||',
'   case when l_unbind_events = ''Y'' then ',
'     ''  $(this.affectedElements).find("div.tree").unbind(".a.click");''||',
'     ''  $(this.affectedElements).find("div.tree").unbind(".a.dblclick");''',
'   else ',
'     ''''',
'   end ||',
'   ''  $(this.affectedElements).find("li a").each(function(index) {'' ||',
'   ''    var i = $(this).html();'' ||',
'   ''    i = i.replace(/''|| l_open_pattern  ||''/g, ''''<'''');''||',
'   ''    i = i.replace(/''|| l_close_pattern ||''/g, ''''>'''');''||',
'   ''    i = i.replace(/''|| l_amp_pattern   ||''/g, ''''&'''');''||',
'   ''    $(this).html(i);''||',
'   '' })'' ||',
'   ''}'';',
'  return l_result;',
'end render_tree_markup;'))
,p_render_function=>'render_tree_markup'
,p_standard_attributes=>'REGION:REQUIRED'
,p_substitute_attributes=>true
,p_subscribe_plugin_settings=>true
,p_help_text=>wwv_flow_utilities.join(wwv_flow_t_varchar2(
'<p>',
'	This plugin enables HTML Markup within an APEX Tree Region which normally escapes all HTML Special characters. The idea is to use replacement characters for &quot;&lt;&quot;, &quot;&gt;&quot; and &quot;&amp;&quot; in the Tree SQL query and to config'
||'ure these in the plugin. The plugin fires as a dynamic action after page load and uses some jQuery logic to activate the HTML markup by changing the replacement characters back to HTML syntax.</p>',
'<p>',
'	<b><u>Example APEX Tree SQL query:</u></b></p>',
'<pre>',
'select case when connect_by_isleaf = 1 then 0',
'            when level = 1             then 1',
'            else                           -1',
'       end as status, ',
'       level, ',
'       case ',
'        when sal &lt; 2500 then ',
'         &#39;[b style=&quot;color: green&quot;]&#39;||&quot;ENAME&quot;||&#39;[/b]&sect;nbsp;[img src=&quot;#IMAGE_PREFIX#Fndokay1.gif&quot; height=&quot;12&quot;]&#39; ',
'        when sal &lt; 4500 then',
'         &#39;[b style=&quot;color: black&quot;]&#39;||&quot;ENAME&quot;||&#39;[/b]&#39; ',
'        else ',
'         &#39;[b style=&quot;color: red&quot;]&#39;||&quot;ENAME&quot;||&#39;[/b]&#39; ',
'       end as title,',
'       null as icon, ',
'       &quot;EMPNO&quot; as value, ',
'       null as tooltip, ',
'       null as link ',
'from &quot;#OWNER#&quot;.&quot;EMP&quot;',
'start with &quot;MGR&quot; is null',
'connect by prior &quot;EMPNO&quot; = &quot;MGR&quot;',
'order siblings by &quot;ENAME&quot;',
'</pre>',
'<p>',
'	<b><u>Plugin configuration:</u></b></p>',
'<ul>',
'	<li>',
'		<b>Pattern for &quot;&lt;&quot;</b>: <b style="color: red">[</b></li>',
'	<li>',
'		<b>Pattern for &quot;&gt;&quot;</b>: <b style="color: red">]</b></li>',
'	<li>',
'		<b>Pattern for &quot;&amp;&quot;</b>: <b style="color: red">&sect;</b></li>',
'</ul>',
''))
,p_version_identifier=>'1.1'
);
